package com.victor.presenter;import com.victor.okhttp.library.annotation.HttpParms;import com.victor.okhttp.library.data.Request;import com.victor.okhttp.library.inject.HttpInject;import com.victor.okhttp.library.module.HttpRequest;import com.victor.okhttp.library.presenter.impl.BasePresenterImpl;import com.victor.view.GankBannerView;import com.victor.view.HeaderView;import java.io.IOException;/* * ----------------------------------------------------------------- * Copyright (C) 2018-2028, by Victor, All rights reserved. * ----------------------------------------------------------------- * File: HeaderPresenterImpl.java * Author: Victor * Date: 2018/9/6 18:25 * Description: * ----------------------------------------------------------------- */public class GankBannerPresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private GankBannerView gankBannerView;	public GankBannerPresenterImpl(GankBannerView gankBannerView) {		this.gankBannerView = gankBannerView;	}	@Override	public void onSuccess(T data) {		if (gankBannerView == null) return;		if (data == null) {			gankBannerView.OnGankBanner(null,"no data response");		} else {			gankBannerView.OnGankBanner(data,"");		}	}	@Override	public void onError(IOException error) {		if (gankBannerView == null) return;		gankBannerView.OnGankBanner(null,error.getMessage());	}	@Override	public Class getViewCls() {		return GankBannerView.class;	}	@Override	public Class getViewImplCls() {		return gankBannerView.getClass();	}	@Override	public void detachView() {		gankBannerView = null;	}	@HttpParms(method = Request.GET,bodyContentType = HttpRequest.mJsonBodyContentType)	@Override	public void sendRequest(String url, H header, T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}