package com.victor.presenter;import com.victor.data.LoginReq;import com.victor.okhttp.library.annotation.HttpParms;import com.victor.okhttp.library.data.Request;import com.victor.okhttp.library.inject.HttpInject;import com.victor.okhttp.library.module.HttpRequest;import com.victor.okhttp.library.presenter.impl.BasePresenterImpl;import com.victor.view.HeaderView;import java.io.IOException;/* * ----------------------------------------------------------------- * Copyright (C) 2018-2028, by Victor, All rights reserved. * ----------------------------------------------------------------- * File: HeaderPresenterImpl.java * Author: Victor * Date: 2018/9/6 18:25 * Description: * ----------------------------------------------------------------- */public class HeaderPresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private HeaderView headerView;	public HeaderPresenterImpl(HeaderView headerView) {		this.headerView = headerView;	}	@Override	public void onSuccess(T data) {		if (headerView == null) return;		if (data == null) {			headerView.OnHeader(null,"no data response");		} else {			headerView.OnHeader(data,"");		}	}	@Override	public void onError(IOException error) {		if (headerView == null) return;		headerView.OnHeader(null,error.getMessage());	}	@Override	public void detachView() {		headerView = null;	}	@HttpParms(method = Request.POST,bodyContentType = HttpRequest.mJsonBodyContentType,			responseCls = LoginReq.class)	@Override	public void sendRequest(String url, H header, T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}