package com.victor.presenter;import com.victor.data.PhoneCodeReq;import com.victor.okhttp.library.annotation.HttpParms;import com.victor.okhttp.library.data.Request;import com.victor.okhttp.library.inject.HttpInject;import com.victor.okhttp.library.module.HttpRequest;import com.victor.okhttp.library.presenter.impl.BasePresenterImpl;import com.victor.view.PhoneCodeView;/* * ----------------------------------------------------------------- * Copyright (C) 2018-2028, by Victor, All rights reserved. * ----------------------------------------------------------------- * File: PhoneCodePresenterImpl.java * Author: Victor * Date: 2018/9/6 18:25 * Description: * ----------------------------------------------------------------- */public class PhoneCodePresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private PhoneCodeView phoneCodeView;	public PhoneCodePresenterImpl(PhoneCodeView phoneCodeView) {		this.phoneCodeView = phoneCodeView;	}	@Override	public void onSuccess(T data) {		if (phoneCodeView == null) return;		if (data == null) {			phoneCodeView.OnPhoneCode(null,"no data response");		} else {			phoneCodeView.OnPhoneCode(data,"");		}	}	@Override	public void onError(String error) {		if (phoneCodeView == null) return;		phoneCodeView.OnPhoneCode(null,error);	}	@Override	public void detachView() {		phoneCodeView = null;	}	@HttpParms(method = Request.POST,bodyContentType = HttpRequest.mDefaultBodyContentType,			responseCls = PhoneCodeReq.class)	@Override	public void sendRequest(String url, H header, T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}