package com.victor.presenter;import com.victor.data.UploadReq;import com.victor.okhttp.library.annotation.HttpParms;import com.victor.okhttp.library.data.Request;import com.victor.okhttp.library.inject.HttpInject;import com.victor.okhttp.library.module.HttpRequest;import com.victor.okhttp.library.presenter.impl.BaseUploadPresenterImpl;import com.victor.view.UploadView;import java.io.IOException;/* * ----------------------------------------------------------------- * Copyright (C) 2018-2028, by Victor, All rights reserved. * ----------------------------------------------------------------- * File: UploadPresenterImpl.java * Author: Victor * Date: 2018/9/6 18:25 * Description: * ----------------------------------------------------------------- */public class UploadPresenterImpl<H,T> extends BaseUploadPresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private UploadView uploadView;	public UploadPresenterImpl(UploadView uploadView) {		this.uploadView = uploadView;	}	@Override	public void onSuccess(T data) {		if (uploadView == null) return;		if (data == null) {			uploadView.OnUpload(null,"no data response");		} else {			uploadView.OnUpload(data,"");		}	}	@Override	public void onError(IOException error) {		if (uploadView == null) return;		uploadView.OnUpload(null,error.getMessage());	}	@Override	public Class getViewCls() {		return UploadView.class;	}	@Override	public Class getViewImplCls() {		return uploadView.getClass();	}	@Override	public void detachView() {		uploadView = null;	}	@HttpParms(method = Request.POST,bodyContentType = HttpRequest.mDefaultBodyContentType)	@Override	public void sendRequest(String url, H header, T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}