package com.victor.presenter;import com.victor.okhttp.library.annotation.HttpParms;import com.victor.okhttp.library.data.Request;import com.victor.okhttp.library.inject.HttpInject;import com.victor.okhttp.library.module.HttpRequest;import com.victor.okhttp.library.presenter.impl.BasePresenterImpl;import com.victor.view.YoutubeView;import java.io.IOException;/* * ----------------------------------------------------------------- * Copyright (C) 2018-2028, by Victor, All rights reserved. * ----------------------------------------------------------------- * File: YoutubePresenterImpl.java * Author: Victor * Date: 2018/9/6 18:25 * Description: * ----------------------------------------------------------------- */public class YoutubePresenterImpl<H,T> extends BasePresenterImpl<H,T> {	/*Presenter作为中间层，持有View和Model的引用*/	private YoutubeView youtubeView;	public YoutubePresenterImpl(YoutubeView youtubeView) {		this.youtubeView = youtubeView;	}	@Override	public void onSuccess(T data) {		if (youtubeView == null) return;		if (data == null) {			youtubeView.OnYoutube(null,"no data response");		} else {			youtubeView.OnYoutube(data,"");		}	}	@Override	public void onError(IOException error) {		if (youtubeView == null) return;		youtubeView.OnYoutube(null,error.getMessage());	}	@Override	public Class getViewCls() {		return YoutubeView.class;	}	@Override	public Class getViewImplCls() {		return youtubeView.getClass();	}	@Override	public void detachView() {		youtubeView = null;	}	@HttpParms(method = Request.GET,bodyContentType = HttpRequest.mDefaultBodyContentType)	@Override	public void sendRequest(String url, H header, T parm) {		HttpInject.inject(this);		super.sendRequest(url,header,parm);	}}